            // Draw second eye with rotation
            ctx.save();
            ctx.translate(eye2X, eye2Y);
            ctx.rotate(movementAngle);
            ctx.fillStyle = 'white';
            ctx.fillRect(-eyeSize, -eyeSize, eyeSize * 2, eyeSize * 2);

            // Draw second pupil looking forward
