          ctx.fill();
        }

        ctx.restore();

        // Draw rotated square eyes exactly like remote snakes
        if (fullSnakeBody.length > 0) {
          const head = fullSnakeBody[0];

          // Calculate movement direction from first two segments
          let movementAngle = 0;
          if (fullSnakeBody.length > 1) {
