          const cappedMass = Math.min(snake.visibleSegments.length, maxMass);
          const scaleFactor = Math.max(0.8, cappedMass / baseMass);

          ctx.font = `${Math.floor(10 * scaleFactor)}px 'Press Start 2P', monospace`;
          ctx.fillStyle = "#ffffff";
