          ctx.fillStyle = 'black';
          ctx.fillRect(
            (Math.cos(cursorAngle - movementAngle) * pupilOffset) - pupilSize,
            (Math.sin(cursorAngle - movementAngle) * pupilOffset) - pupilSize,
