  const [cashOutAmount, setCashOutAmount] = useState("");
  const [isRefreshing, setIsRefreshing] = useState(false);

  if (!user) return null;

  const handleCopyAddress = () => {
    const mockAddress = `${user.id.slice(0, 8)}...${user.id.slice(-8)}`;
    navigator.clipboard.writeText(mockAddress);
    toast({
      title: "Address Copied",
      description: "Wallet address copied to clipboard",
    });
  };

  const handleRefreshBalance = async () => {
    setIsRefreshing(true);
    try {
      // Simulate balance refresh - in real app would call blockchain API
      await new Promise(resolve => setTimeout(resolve, 1000));
      toast({
        title: "Balance Refreshed",
        description: "Wallet balance has been updated",
      });
    } finally {
      setIsRefreshing(false);
    }
  };

  const handleAddFunds = async () => {
    const amount = parseFloat(addAmount);
    if (isNaN(amount) || amount <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid amount",
        variant: "destructive",
      });
      return;
    }

    try {
      const response = await apiRequest("POST", `/api/users/${user.id}/update-balance`, {
        amount: amount
      });
      const updatedUser = await response.json();
      updateUser(updatedUser);

      setIsAddFundsOpen(false);
      setAddAmount("");

      toast({
        title: "Funds Added",
        description: `Successfully added $${amount.toFixed(2)} to your wallet`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add funds",
        variant: "destructive",
      });
    }
  };

  const handleCashOut = async () => {
    const amount = parseFloat(cashOutAmount);
    const userBalance = parseFloat(user.balance);

    if (isNaN(amount) || amount <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid amount",
        variant: "destructive",
      });
      return;
    }

    if (amount > userBalance) {
      toast({
        title: "Insufficient Balance",
        description: "You don't have enough funds to cash out this amount",
        variant: "destructive",
      });
      return;
    }

    try {
      const response = await apiRequest("POST", `/api/users/${user.id}/update-balance`, {
        amount: -amount
      });
      const updatedUser = await response.json();
      updateUser(updatedUser);

      setIsCashOutOpen(false);
      setCashOutAmount("");

      toast({
        title: "Cash Out Successful",
        description: `Successfully withdrew $${amount.toFixed(2)} from your wallet`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to cash out",
        variant: "destructive",
      });
    }
  };

  return (
    <>
      <Card className="bg-dark-card border-dark-border border-l-4 border-l-neon-green">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg font-bold text-white">Wallet</CardTitle>
            <div className="flex space-x-2">
              <Button
                variant="ghost"
                size="icon"
                onClick={handleCopyAddress}
                className="text-gray-400 hover:text-white transition-colors"
                title="Copy wallet address"
              >
                <Copy className="w-5 h-5" />
              </Button>

              <Button
                variant="ghost"
                size="icon"
                onClick={handleRefreshBalance}
                disabled={isRefreshing}
                className="text-gray-400 hover:text-white transition-colors"
                title="Refresh balance"
              >
                <RefreshCw className={`w-5 h-5 ${isRefreshing ? 'animate-spin' : ''}`} />
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Balance display */}
          <div className="text-center">
            <div className="neon-yellow text-4xl font-bold mb-1">
              ${parseFloat(user.balance).toFixed(2)}
            </div>
            <div className="text-gray-400 text-sm">
              {parseFloat(user.solBalance).toFixed(8)} SOL
            </div>
          </div>

          {/* Action buttons */}
          <div className="grid grid-cols-2 gap-3">
            <Dialog open={isAddFundsOpen} onOpenChange={setIsAddFundsOpen}>
              <DialogTrigger asChild>
                <Button className="bg-neon-green text-black font-semibold hover:bg-green-400 transition-colors">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Funds
                </Button>
              </DialogTrigger>
              <DialogContent className="bg-dark-card border-dark-border text-white">
                <DialogHeader>
                  <DialogTitle>Add Funds</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <Input
                    type="number"
                    placeholder="Amount in USD"
                    value={addAmount}
                    onChange={(e) => setAddAmount(e.target.value)}
                    className="bg-dark-bg border-dark-border"
                    min="0"
                    step="0.01"
                  />
                  <div className="text-sm text-gray-400">
                    Minimum: $1.00 â€¢ Maximum: $500.00
                  </div>
                  <Button 
                    onClick={handleAddFunds}
                    className="w-full bg-neon-green text-black hover:bg-green-400"
                  >
                    Add Funds
                  </Button>
                </div>
              </DialogContent>
            </Dialog>

            <Dialog open={isCashOutOpen} onOpenChange={setIsCashOutOpen}>
              <DialogTrigger asChild>
                <Button className="bg-neon-blue text-white font-semibold hover:bg-blue-600 transition-colors">
                  <DollarSign className="w-4 h-4 mr-2" />
                  Cash Out
                </Button>
              </DialogTrigger>
              <DialogContent className="bg-dark-card border-dark-border text-white">
                <DialogHeader>
                  <DialogTitle>Cash Out</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <Input
                    type="number"
                    placeholder="Amount in USD"
                    value={cashOutAmount}
                    onChange={(e) => setCashOutAmount(e.target.value)}
                    className="bg-dark-bg border-dark-border"
                    min="0"
                    max={parseFloat(user.balance)}
                    step="0.01"
                  />
                  <div className="text-sm text-gray-400">
                    Available: ${parseFloat(user.balance).toFixed(2)} â€¢ Minimum: $1.00
                  </div>
                  <Button 
                    onClick={handleCashOut}
                    className="w-full bg-neon-blue text-white hover:bg-blue-600"
                  >
                    Cash Out
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardContent>
      </Card>
    </>
  );
}

server/gameServer.ts.content:
import { WebSocketServer, WebSocket } from "ws";
import type { Server } from "http";

interface GameWorld {
  bots: any[];
  players: Map<string, any>;
  initialized: boolean;
}

interface GameServerInstance {
  wss: WebSocketServer;
  activePlayers: Map<string, any>;
  gameWorld: GameWorld;
  serverId: number;
}

export class MultiServerManager {
  private static readonly MAX_PLAYERS_PER_SERVER = 8;
  private gameServers = new Map<number, GameServerInstance>();
  private nextServerId = 0;

  constructor(private httpServer: Server) {}

  createGameServer(serverId: number): GameServerInstance {
    const wss = new WebSocketServer({ 
      server: this.httpServer, 
      path: `/ws/${serverId}`,
      perMessageDeflate: false, // Disable compression
      maxPayload: 1024 * 1024, // 1MB max payload
      skipUTF8Validation: false, // Keep UTF8 validation
      clientTracking: true
    });

    const activePlayers = new Map<string, any>();

    const gameWorld: GameWorld = {
      bots: [],
      players: new Map(),
      initialized: false
    };

    const serverInstance: GameServerInstance = {
      wss,
      activePlayers,
      gameWorld,
      serverId
    };

    this.gameServers.set(serverId, serverInstance);
    this.setupServerWebSocket(serverInstance);
    console.log(`ðŸš€ Created game server ${serverId} (path: /ws/${serverId})`);

    return serverInstance;
  }

  getAvailableServer(): GameServerInstance {
    // Find server with available slots
    for (const [id, server] of Array.from(this.gameServers.entries())) {
      if (server.activePlayers.size < MultiServerManager.MAX_PLAYERS_PER_SERVER) {
        return server;
      }
    }

    // All servers full, create new one
    const newServer = this.createGameServer(this.nextServerId++);
    console.log(`ðŸ“ˆ All servers full! Created new server ${newServer.serverId}`);
    return newServer;
  }

  initialize() {
    // Create initial server
    this.createGameServer(this.nextServerId++);

    // Setup legacy WebSocket for backwards compatibility
    const legacyWss = new WebSocketServer({ 
      server: this.httpServer, 
      path: '/ws',
      perMessageDeflate: false // Disable compression to fix RSV1 frame errors
    });
    legacyWss.on('connection', (ws) => {
      ws.close(1000, 'Please connect to /ws/0 or use the server selection API');
    });

    // Broadcast game state for all servers
    this.startBroadcasting();
  }

  private setupServerWebSocket(serverInstance: GameServerInstance) {
    const { wss, activePlayers, gameWorld, serverId } = serverInstance;

    wss.on("connection", (ws: any) => {
      // Check server capacity
      if (activePlayers.size >= MultiServerManager.MAX_PLAYERS_PER_SERVER) {
        console.log(`Server ${serverId} full, rejecting connection`);
        ws.close(1013, 'Server full');
        return;
      }

      const playerId = `player_${Date.now()}_${Math.random()}`;
      console.log(`Player ${playerId} joined server ${serverId}. Active: ${wss.clients.size}`);

      ws.playerId = playerId;

      const colors = ['#d55400', '#4ecdc4', '#ff6b6b', '#45b7d1', '#96ceb4', '#feca57', '#ff9ff3', '#54a0ff'];
      const playerColor = colors[activePlayers.size % colors.length];

      const player = {
        id: playerId,
        segments: [],
        color: playerColor,
        money: 1.00,
        lastUpdate: Date.now()
      };

      activePlayers.set(playerId, player);
      gameWorld.players.set(playerId, player);

      // Initialize game world if needed
      this.initializeGameWorld(gameWorld);

      // Send consolidated welcome message with all initial data
      try {
        const welcomeMessage = {
          type: 'welcome',
          playerId: playerId,
          initialState: {
            players: Array.from(activePlayers.values()),
            bots: gameWorld.bots,
            gameWorld: Array.from(gameWorld.players.values())
          }
        };

        // Send smaller welcome message to avoid frame issues
        const simpleWelcome = {
          type: 'welcome',
          playerId: playerId
        };

        if (ws.readyState === WebSocket.OPEN) {
          ws.send(JSON.stringify(simpleWelcome));

          // Send other data separately with delay
          setTimeout(() => {
            if (ws.readyState === WebSocket.OPEN) {
              ws.send(JSON.stringify({
                type: 'initial_players', 
                players: Array.from(activePlayers.values())
              }));
            }
          }, 50);
        }
      } catch (error) {
        console.error(`Error sending welcome message to ${playerId}:`, error);
      }

      ws.on("message", (message: any) => {
        try {
          const data = JSON.parse(message.toString());
          if (data.type === 'update') {
            this.handlePlayerUpdate(data, playerId, serverInstance, ws);
          }
        } catch (error) {
          console.error("WebSocket message error:", error);
          // Don't close connection for parsing errors
        }
      });

      ws.on("close", (code: number, reason: Buffer) => {
        console.log(`Player ${playerId} left server ${serverId}. Code: ${code}, Remaining: ${wss.clients.size - 1}`);
        activePlayers.delete(playerId);
        gameWorld.players.delete(playerId);
      });

      ws.on("error", (error: any) => {
        console.error(`WebSocket error for player ${playerId}:`, error);
        // Don't immediately close on protocol errors - let the client retry
        if (!error.message?.includes('RSV1') && !error.message?.includes('Invalid WebSocket frame')) {
          activePlayers.delete(playerId);
          gameWorld.players.delete(playerId);
        }
      });
    });
  }

  private handlePlayerUpdate(data: any, playerId: string, serverInstance: GameServerInstance, ws: any) {
    const { activePlayers, gameWorld } = serverInstance;
    const existingPlayer = activePlayers.get(playerId);

    // Enforce 100-segment and 100-mass limits
    const MAX_SEGMENTS = 100;
    const MAX_MASS = 100;
    const segments = data.segments || [];
    const limitedSegments = segments.length > MAX_SEGMENTS ? segments.slice(0, MAX_SEGMENTS) : segments;
    const limitedMass = Math.min(data.totalMass || 6, MAX_MASS);

    const player = {
      id: playerId,
      segments: limitedSegments,
      color: existingPlayer?.color || '#d55400',
      money: data.money || 1.00,
      totalMass: limitedMass,
      segmentRadius: data.segmentRadius || 8,
      visibleSegmentCount: Math.min(data.visibleSegmentCount || 0, MAX_SEGMENTS),
      lastUpdate: Date.now()
    };

    // Check for collisions with other players
    const currentPlayerHead = data.segments && data.segments.length > 0 ? data.segments[0] : null;
    if (currentPlayerHead && data.segmentRadius) {
      let collisionDetected = false;

      for (const [otherPlayerId, otherPlayer] of Array.from(gameWorld.players.entries())) {
        if (otherPlayerId === playerId) continue;
        if (!otherPlayer.segments || otherPlayer.segments.length === 0) continue;

        for (const segment of otherPlayer.segments) {
          const dx = currentPlayerHead.x - segment.x;
          const dy = currentPlayerHead.y - segment.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const collisionThreshold = data.segmentRadius + (otherPlayer.segmentRadius || 8) - 2;

          if (distance < collisionThreshold) {
            collisionDetected = true;
            activePlayers.delete(playerId);
            gameWorld.players.delete(playerId);

            if (ws.readyState === WebSocket.OPEN) {
              ws.send(JSON.stringify({
                type: 'death',
                reason: 'collision',
                crashedInto: otherPlayerId
              }));
            }

            console.log(`ðŸ’€ Player ${playerId} removed from server (collision)`);
            break;
          }
        }
        if (collisionDetected) break;
      }

      if (!collisionDetected) {
        activePlayers.set(playerId, player);
        gameWorld.players.set(playerId, player);
      }
    } else {
      activePlayers.set(playerId, player);
      gameWorld.players.set(playerId, player);
    }
  }

  private initializeGameWorld(gameWorld: GameWorld) {
    if (gameWorld.initialized) return;

    gameWorld.bots = [];
    // Food system completely removed per user request

    gameWorld.initialized = true;
    console.log('Game world initialized (no food, multiplayer only)');
  }

  private startBroadcasting() {
    setInterval(() => {
      this.gameServers.forEach((serverInstance) => {
        const { wss, gameWorld, serverId } = serverInstance;

        if (wss.clients.size > 0) {
          const worldMessage = JSON.stringify({
            type: 'gameWorld',
            bots: gameWorld.bots,
            players: Array.from(gameWorld.players.values())
          });

          console.log(`Broadcasting to ${wss.clients.size} clients on server ${serverId}: ${gameWorld.players.size} players`);

          wss.clients.forEach((client: any) => {
            if (client.readyState === WebSocket.OPEN) {
              try {
                client.send(worldMessage);
              } catch (error) {
                console.error('Broadcast error:', error);
                client.terminate();
              }
            } else {
              client.terminate();
            }
          });
        }
      });
    }, 100);
  }
}

server/httpMultiplayer.ts.content:
import type { Express } from "express";

interface Player {
  id: string;
  segments: Array<{ x: number; y: number }>;
  color: string;
  money: number;
  totalMass: number;
  segmentRadius: number;
  visibleSegmentCount: number;
  lastUpdate: number;
}

interface GameRoom {
  players: Map<string, Player>;
  lastActivity: number;
}

export class HttpMultiplayerManager {
  private static readonly MAX_PLAYERS_PER_ROOM = 8;
  private static readonly ROOM_TIMEOUT = 30000; // 30 seconds
  private gameRooms = new Map<number, GameRoom>();
  private nextRoomId = 0;

  setupRoutes(app: Express) {
    // Get available room
    app.get('/api/multiplayer/room', (req, res) => {
      const room = this.getAvailableRoom();
      res.json({ roomId: room.id });
    });

    // Update player position
    app.post('/api/multiplayer/update', (req, res) => {
      const { roomId, playerId, segments, color, money, totalMass, segmentRadius, visibleSegmentCount } = req.body;

      if (!roomId || !playerId) {
        return res.status(400).json({ error: 'Missing roomId or playerId' });
      }

      const room = this.gameRooms.get(roomId);
      if (!room) {
        return res.status(404).json({ error: 'Room not found' });
      }

      // Enforce 100-segment and 100-mass limits
      const MAX_SEGMENTS = 100;
      const MAX_MASS = 100;
      const limitedSegments = (segments || []).slice(0, MAX_SEGMENTS);
      const limitedMass = Math.min(totalMass || 6, MAX_MASS);

      const player: Player = {
        id: playerId,
        segments: limitedSegments,
        color: color || '#d55400',
        money: money || 1.00,
        totalMass: limitedMass,
        segmentRadius: segmentRadius || 8,
        visibleSegmentCount: Math.min(visibleSegmentCount || 0, MAX_SEGMENTS),
        lastUpdate: Date.now()
      };

      room.players.set(playerId, player);
      room.lastActivity = Date.now();

      res.json({ success: true });
    });

    // Get room state
    app.get('/api/multiplayer/state/:roomId', (req, res) => {
      const roomId = parseInt(req.params.roomId);
      const room = this.gameRooms.get(roomId);

      if (!room) {
        return res.status(404).json({ error: 'Room not found' });
      }

      // Remove stale players (haven't updated in 10 seconds)
      const now = Date.now();
      Array.from(room.players.entries()).forEach(([playerId, player]) => {
        if (now - player.lastUpdate > 10000) {
          room.players.delete(playerId);
        }
      });

      const players = Array.from(room.players.values());
      res.json({ players });
    });

    // Cleanup old rooms periodically
    setInterval(() => {
      const now = Date.now();
      Array.from(this.gameRooms.entries()).forEach(([roomId, room]) => {
        if (now - room.lastActivity > HttpMultiplayerManager.ROOM_TIMEOUT) {
          this.gameRooms.delete(roomId);
          console.log(`ðŸ§¹ Cleaned up inactive room ${roomId}`);
        }
      });
    }, 15000); // Check every 15 seconds
  }

  private getAvailableRoom(): { id: number } {
    // Find room with available slots
    for (const [id, room] of Array.from(this.gameRooms.entries())) {
      if (room.players.size < HttpMultiplayerManager.MAX_PLAYERS_PER_ROOM) {
        return { id };
      }
    }

    // All rooms full, create new one
    const newRoomId = this.nextRoomId++;
    const newRoom: GameRoom = {
      players: new Map(),
      lastActivity: Date.now()
    };

    this.gameRooms.set(newRoomId, newRoom);
    console.log(`ðŸš€ Created new multiplayer room ${newRoomId}`);

    return { id: newRoomId };
  }
}

